sys_script_include.script:
var ADMPCommonUtil = Class.create();
ADMPCommonUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	loadDefaultValues: function()
	{
		var sys_id = this.getParameter('sys_id');
		var requestDetails = new ADMPIncidentHandler().getIncidentDetails(sys_id);
		requestDetails = new global.JSON().decode(requestDetails);
		requestDetails.domainList = new ADMPDomainListHandler().getDomainList();	
		return new global.JSON().encode(requestDetails);
	},
	getTemplateList: function()
	{		
		var formData = {};
		if(new ADMPServerDetails().isServerConfigured())
		{
			formData = new ADMPAPIUtil().getTemplateList();
			formData = new global.JSON().decode(formData);

			var domainNameList = new ADMPDomainListHandler().getDomainList();
			formData.domainList = domainNameList;
		}
		else
		{
			formData.status = 'false';
			formData.responseBody = 'Please configure ADManager server or Contact your system administrator.';
		}
		return new global.JSON().encode(formData);
	},
	type: 'ADMPCommonUtil'
});
