sys_script_include.script:
var ADMPUserListHandler = Class.create();
ADMPUserListHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getADUsersList: function()
	{
		var resultJSON = {};
		var searchText = this.getParameter('searchText');
		var domainName = this.getParameter('domainName');
		var startIndex = this.getParameter('startIndex');
		var range = this.getParameter('range');
		var responseJSON = new ADMPAPIUtil().getADUsersList(domainName, searchText, startIndex, range);
		responseJSON = new global.JSON().decode(responseJSON);

		if(responseJSON.status == 'true')
		{
			var userListJSON =  new global.JSON().decode(responseJSON.responseBody);
			var count = userListJSON.count;
			var userList = userListJSON.UsersList;

			resultJSON.count = count;
			resultJSON.startIndex = startIndex;
			var endIndex = parseInt(startIndex)+parseInt(range)-1;
			resultJSON.endIndex = (endIndex<=count)?endIndex:count;
			resultJSON.UsersList = userList;
		} 
		else
		{
			gs.addInfoMessage(responseJSON.responseBody);
		}
		return new global.JSON().encode(resultJSON);
	//	return '{"startIndex":1,"endIndex":10,"count":6,"UsersList":[{"DISPLAY_NAME":"admin-xp2","SID_STRING":"S-1-5-21-1867688552-3649366528-3325780993-5762","DISTINGUISHED_NAME":"CN=admin-xp2,CN=Users,DC=csez,DC=zohocorpin,DC=com","OBJECT_GUID":"{2ABE4653-B6CE-4F8C-AB59-7AA2431A8E17}","LOGON_NAME":"admin-xp2@csez.zohocorpin.com","OU_NAME":"Users","SAM_ACCOUNT_NAME":"admin-xp2"}]}';
	},
    type: 'ADMPUserListHandler'
});
