sys_script_include.script:
var ADManagerMIDServerHandler = Class.create();
ADManagerMIDServerHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	isMIDServerConfigured: function()
	{
		//ecc_agent
		var midServerRecord = new GlideRecord('ecc_agent');
		midServerRecord.addQuery('name', 'SNCMIDServer_ADManager');
		midServerRecord.query();
		if (midServerRecord.getRowCount()>0) 
			 return true;
		else
			return false;
	},
	isMIDServerUP: function()
	{
		var status = false;
		var midServerRecord = new GlideRecord('ecc_agent');
		midServerRecord.addQuery('name', 'SNCMIDServer_ADManager');
		midServerRecord.query();
		if(midServerRecord.next())
		{
			if(midServerRecord.status == 'Up' && midServerRecord.Validated == 'Yes')
				status = true;
		}
		else
			status = false;
	},
	setMIDServerToRestMessages: function(sys_id)
	{
		var restMessageRecord = new GlideRecord('sys_rest_message_fn');
		restMessageRecord.addQuery('qualified_name', 'IN', 'ADManagerUnlockUser [ post ], ADManagerADUserList [ post ], ADManagerTemplateNames [ post ], ADManagerEnableUser [ post ], ADManagerLogin [ post ], ADManagerResetPwd [ post ], ADManagerCreateUser [ post ], ADManagerDeleteUser [ post ]');
		restMessageRecord.query();
		while(restMessageRecord.next())
		{
			restMessageRecord.use_mid_server = sys_id;
			restMessageRecord.update();
		}
	},
    type: 'ADManagerMIDServerHandler'
});
