sys_script_include.script:
var ADMPActionHandler = Class.create();
ADMPActionHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	addActionList:function(domainsVsActionList)
	{
		this.removeList();
		var id = 0;
		for(var domainName in domainsVsActionList){
			var domain_id = new ADMPDomainListHandler().getDomainId(domainName);
			var actionList = domainsVsActionList[domainName];
			actionList  = new global.JSON().decode(actionList);

			for(var i=0; i<actionList.length;i++)
			{
				var action_id = actionList[i].toString(); 
				var actionRecord = new GlideRecord('x_96574_admanager_admpactionslist');
				actionRecord.initialize(); 
				actionRecord.unique_id = ++id;
				actionRecord.domain_id = domain_id; 
				actionRecord.action_id = action_id; 
				actionRecord.insert();
			}
		}
	},
	removeList: function()
	{
		var gr = new GlideRecord('x_96574_admanager_admpactionslist');
		gr.deleteMultiple();
	},
	updateADManagerState: function(state)
	{
		// To activate/deactivate all menu modules
		var moduleRecord = new GlideRecord('sys_app_module');
		moduleRecord.addQuery('title', 'IN', 'Create a User, Delete Users, Disable Users, Enable Users, Reset User Password, Unlock Users');
		moduleRecord.query();
		while(moduleRecord.next())
		{
			moduleRecord.active = state;
			moduleRecord.update();
		}

		// To activate/deactivate all context menu actions

		var actionRecord = new GlideRecord('sys_ui_action');
		actionRecord.addQuery('title', 'IN', 'Create a User in AD, Delete Users in AD, Disable Users in AD, Enable Users in AD, Reset User Password in AD, Unlock Users in AD');
		actionRecord.query();
		while(actionRecord.next())
		{
			actionRecord.active = state;
			actionRecord.update();
		}
	},
    type: 'ADMPActionHandler'
});
