sys_script_include.script:
var ADMPErrorHandler = Class.create();
ADMPErrorHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	handleError: function(responseCode, responseBody)
	{		
		var responseJSON = {};
		if(responseCode != '200' || responseBody == '' || responseBody == 'null')
		{
			responseJSON.status = 'false';
			responseJSON.responseCode = responseCode;
			responseJSON.responseBody = (responseBody == '' || responseBody == null)?'Couldn\'t connect to server':responseBody;
		}
		else if((new global.JSON().decode(responseBody)).ERROR_CODE != null)
		{
			responseBody = new global.JSON().decode(responseBody);
			responseJSON.status = 'false';
			responseJSON.responseCode = responseBody.ERROR_CODE;
			responseJSON.responseBody = responseBody.STATUS_MESSAGE;
		}
		else if((new global.JSON().decode(responseBody)))
		{
			var responseBodyy = new global.JSON().decode(responseBody);
			if(responseBodyy[0])
			{
				var status = responseBodyy[0].status;
				if(status == 'SEVERE')
				{
					responseJSON.status = 'false';
					responseJSON.responseBody = responseBody;
				}
				else
				{
					responseJSON.status = 'true';
					responseJSON.responseCode = responseCode;
					responseJSON.responseBody =responseBody;
				}
			}
			else
			{
				responseJSON.status = 'true';
				responseJSON.responseCode = responseCode;
				responseJSON.responseBody =responseBody;
			}
		}
		else			
		{
			responseJSON.status = 'true';
			responseJSON.responseCode = responseCode;
			responseJSON.responseBody =responseBody;
		}
		return responseJSON;
	},
	type: 'ADMPErrorHandler'
});
